@push('styles')
    <style>
        .social-sidebar {
            background: white;
            border: 2px solid #caecfa;
            border-radius: 8px;
            padding: 15px 10px;
            box-shadow: 0 2px 8px rgba(0, 0, 0, 0.1);
            width: 70px;
            position: sticky;
            top: 85px;
            align-self: flex-start;
        }

        .social-button {
            display: flex;
            align-items: center;
            justify-content: center;
            width: 100%;
            height: 60px;
            margin-bottom: 15px;
            border: none;
            background: white;
            cursor: pointer;
            transition: transform 0.2s, box-shadow 0.2s;
            border-radius: 4px;
            position: relative;
        }

        .social-button:hover {
            transform: translateY(-2px);
            box-shadow: 0 4px 10px rgba(0, 0, 0, 0.12);
        }

        .social-button:active {
            transform: translateY(0);
        }

        .icon {
            width: 28px;
            height: 28px;
        }

        .facebook-btn {
            color: #1877f2;
        }

        .twitter-btn {
            color: #1da1f2;
        }

        .comment-btn {
            background: transparent;
            color: white;
            font-size: 13px;
            font-weight: bold;
            padding: 12px;
            height: auto;
            border-radius: 6px;
            position: relative;
            overflow: hidden;
        }

        .comment-btn svg {
            position: absolute;
            top: 0;
            left: 50%;
            transform: translateX(-50%);
            width: 100%;
            height: 100%;
            z-index: 0;
        }

        .comment-btn .text {
            position: relative;
            z-index: 1;
            text-shadow: 0 1px 2px rgba(0, 0, 0, 0.2);
            line-height: 1.2;
        }

        .news-btn {
            background: transparent;
            color: #4368B0;
            font-size: 13px;
            font-weight: 500;
            padding: 10px;
            height: auto;
            border-radius: 6px;
            position: relative;
            overflow: hidden;
        }

        .news-btn svg {
            position: absolute;
            top: 50%;
            left: 50%;
            transform: translate(-50%, -50%);
            width: 100%;
            height: auto;
            z-index: 0;
        }

        .divider {
            height: 1px;
            background: #caecfa;
            margin: 10px 0;
        }

        .social-toast {
            position: fixed;
            bottom: 20px;
            right: 20px;
            background: #323232;
            color: white;
            padding: 14px 22px;
            border-radius: 4px;
            display: none;
            animation: slideIn 0.3s ease-out;
            z-index: 9999;
        }

        .social-toast.show {
            display: block;
        }

        @keyframes slideIn {
            from {
                transform: translateY(100px);
                opacity: 0;
            }

            to {
                transform: translateY(0);
                opacity: 1;
            }
        }

        @media (max-width: 768px) {
            .social-sidebar {
                width: 50px;
                padding: 10px 6px;
            }

            .social-button {
                height: 45px;
                margin-bottom: 10px;
            }

            .icon {
                width: 20px;
                height: 20px;
            }

            .comment-btn,
            .news-btn {
                font-size: 10px;
                padding: 8px 4px;
            }

            .divider {
                margin: 6px 0;
            }
        }

        @media (max-width: 480px) {
            .social-sidebar {
                width: 45px;
                padding: 8px 4px;
            }

            .social-button {
                height: 40px;
                margin-bottom: 8px;
            }

            .icon {
                width: 18px;
                height: 18px;
            }

            .comment-btn,
            .news-btn {
                font-size: 9px;
                padding: 6px 2px;
            }
        }
    </style>
@endpush

